import React from "react";
import {
  Container,
  Box,
  Typography,
  TextField,
  Button,
} from "@material-ui/core";

export default class ResturantUpdate extends React.PureComponent {
  constructor(props) {
    super(props);

    this.state = {
      id: "",
      name: "",
      username:'',
      password:'',
      email: "",
      contact: "",
      address: "",
    };

    fetch(
      "http://localhost:8080/users/" + this.props.match.params.id
    ).then((resp) => {
      resp.json().then((data) => {
        // console.log(data)
        this.setState({
          id: data.id,
          name: data.name,
          username:data.username,
          password:data.password,
          email: data.email,
          contact:data.contact,
          address: data.address,

        });
      });
    });

    this.handleChange = this.handleChange.bind();
    this.update = this.update.bind();
  }

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  update = () => {
    if(this.state.name===null || this.state.username || this.state.password || this.state.email===null || this.state.contact || this.state.address===null)
    {
        alert('Please Fill All the fields...');
    }else{
    fetch("http://localhost:8080/users/" + this.state.id, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(this.state),
    }).then((resp) => {
      resp.json().then((result) => {
        // console.log(result)
        alert("User has been Updated");
      });
    });
  }
  };

  render() {
    return (
      <Container maxWidth="sm">
        <h1 style={{ color: "blue", fontSize: "50px" }}>Welcome Admin</h1>
        <br />
        <Box
          bgcolor="white"
          boxShadow="2"
          borderRadius="12px"
          textAlign="center"
          p="24px"
          mt="50px"
        >
          <Typography variant="h5" color="primary">
            USER UPDATE
          </Typography>

          <TextField
            label="Name"
            value={this.state.name}
            type="text"
            name="name"
            onChange={this.handleChange}
            variant="outlined"
            fullWidth
            color="secondary"
            margin="normal"
            size="small"
          />

          <TextField
            label="UserName"
            value={this.state.username}
            type="text"
            name="username"
            onChange={this.handleChange}
            variant="outlined"
            fullWidth
            color="secondary"
            margin="normal"
            size="small"
          />

          <TextField
            label="Password"
            value={this.state.password}
            type="password"
            name="password"
            onChange={this.handleChange}
            variant="outlined"
            fullWidth
            color="secondary"
            margin="normal"
            size="small"
          />

          <TextField
            label="Email Id"
            value={this.state.email}
            type="email"
            name="email"
            onChange={this.handleChange}
            variant="outlined"
            fullWidth
            color="secondary"
            margin="normal"
            size="small"
          />

          <TextField
            label="Contact No."
            value={this.state.contact}
            type="text"
            name="contact"
            onChange={this.handleChange}
            variant="outlined"
            fullWidth
            color="secondary"
            margin="normal"
            size="small"
          />

          <TextField
            label="Restaurant Address"
            type="text"
            name="address"
            value={this.state.address}
            onChange={this.handleChange}
            variant="outlined"
            fullWidth
            color="secondary"
            margin="normal"
            size="small"
          />

          <br />
          <br />
          <Button
            disableElevation
            variant="contained"
            color="primary"
            fullWidth
            onClick={this.update}
          >
            Update User
          </Button>
        </Box>
      </Container>
    );
  }
}
