import React from "react";
import {
  Container,
  Box,
  Typography,
  TextField,
  Button,
  MenuItem,
} from "@material-ui/core";

export default class ResturantUpdate extends React.PureComponent {
  constructor(props) {
    super(props);

    this.state = {
      id: "",
      name: "",
      email: "",
      address: "",
      Rating: "",
      categoryId: "",
      list: [],
    };

    fetch(
      "http://localhost:8080/restaurant/" + this.props.match.params.id
    ).then((resp) => {
      resp.json().then((data) => {
        // console.log(data)
        this.setState({
          id: data.id,
          name: data.name,
          email: data.price,
          address: data.time,
        });
      });
    });

    fetch("http://localhost:8080/categories/").then((resp) => {
      resp.json().then((data) => {
        console.log(data);
        this.setState({
          list: data,
        });
        console.log(data);
      });
    });

    console.log(this.state.list.id + " hi");

    this.handleChange = this.handleChange.bind();
    this.update = this.update.bind();
  }

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  update = () => {
    fetch("http://localhost:8080/restaurant/" + this.state.id, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(this.state),
    }).then((resp) => {
      resp.json().then((result) => {
        // console.log(result)
        alert("Resturant has been Updated");
      });
    });
  };

  render() {
    return (
      <Container maxWidth="sm">
        <h1 style={{ color: "blue", fontSize: "50px" }}>Welcome Admin</h1>
        <br />
        <Box
          bgcolor="white"
          boxShadow="2"
          borderRadius="12px"
          textAlign="center"
          p="24px"
          mt="50px"
        >
          <Typography variant="h5" color="primary">
            UPDATE CATEGORY
          </Typography>

          <TextField
            label="category"
            placeholder={this.state.id}
            id="outlined-size-small"
            type="id"
            name="id"
            onChange={this.handleChange}
            variant="outlined"
            fullWidth
            color="secondary"
            margin="normal"
            size="small"
          />

          <TextField
            label="Availability"
            placeholder={this.state.name}
            id="outlined-size-small"
            type="text"
            name="name"
            onChange={this.handleChange}
            variant="outlined"
            fullWidth
            color="secondary"
            margin="normal"
            size="small"
          />

          <TextField
            label="Start Price"
            id="outlined-size-small"
            type="text"
            name="price"
            onChange={this.handleChange}
            variant="outlined"
            fullWidth
            color="secondary"
            margin="normal"
            size="small"
          />

          <TextField
            label="Timings"
            id="outlined-size-small"
            type="text"
            name="time"
            onChange={this.handleChange}
            variant="outlined"
            fullWidth
            color="secondary"
            margin="normal"
            size="small"
          />


          <br />
          <br />
          <Button
            disableElevation
            variant="contained"
            color="primary"
            fullWidth
            onClick={this.update}
          >
            Update Category
          </Button>
        </Box>
      </Container>
    );
  }
}
