import React, { Component } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Drawer from "@material-ui/core/Drawer";
import AppBar from "@material-ui/core/AppBar";
import CssBaseline from "@material-ui/core/CssBaseline";
import Toolbar from "@material-ui/core/Toolbar";
import List from "@material-ui/core/List";
import Typography from "@material-ui/core/Typography";
import Divider from "@material-ui/core/Divider";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import { Home } from "@material-ui/icons";
import { Create } from "@material-ui/icons";
import { Update } from "@material-ui/icons";
import { ViewList } from "@material-ui/icons";
import { Search } from "@material-ui/icons";
import { LockOpen } from "@material-ui/icons";
import { ExitToApp } from "@material-ui/icons";
import logo from "../media/logo.PNG";
import { BrowserRouter as Router, Link, Switch, Route } from "react-router-dom";

import HomeRestro from "../Components/Home";
import ListRestro from "../Components/List";
import CreateRestro from "../Components/Create";
import SearchRestro from "../Components/Search";
import UpdateRestro from "../Components/Update";
import Logout from "./Logout";
import Login from "./Login";
import { Redirect } from "react-router-dom";
import Protect from "./Protect";
import "../App.css";
import CreateCat from "../Components/AddCategory";
import ListCat from "../Components/ListCategory";
import UpdateCat from "../Components/UpdateCat";
import Base from './Base'

const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
  },
  appBar: {
    zIndex: theme.zIndex.drawer + 1,
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0,
  },
  drawerPaper: {
    width: drawerWidth,
  },
  drawerContainer: {
    overflow: "auto",
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },
  link: {
    textDecoration: "none",
    color: theme.palette.text.primary,
  },
}));

export default function ClippedDrawer() {
  const classes = useStyles();

  return (
    <Router>
      <div className={classes.root}>
        <CssBaseline />
        <AppBar position="fixed" className={classes.appBar}>
          <Toolbar>
            <Link to="/" className="imgLink">
              <Typography variant="h6" noWrap>
                <img src={logo} height="40px" style={{ marginRight: "16px" }} />
                <Typography variant="h4" display="inline">
                  Tamra Restaurant Admin
                </Typography>
              </Typography>
            </Link>
          </Toolbar>
        </AppBar>
        <Drawer
          className={classes.drawer}
          variant="permanent"
          classes={{
            paper: classes.drawerPaper,
          }}
        >
          <Toolbar />
          <div className={classes.drawerContainer}>
            <List>
              <Link to="/Home" className="link">
                <ListItem button>
                  <ListItemIcon>
                    <Home />
                  </ListItemIcon>
                  <ListItemText primary="Home" />
                </ListItem>
              </Link>
            </List>

            <List>
              <Link to="/List" className="link">
                <ListItem button>
                  <ListItemIcon>
                    <ViewList />
                  </ListItemIcon>
                  <ListItemText primary="List Restaurant" />
                </ListItem>
              </Link>
            </List>

            <List>
              <Link to="/Create" className="link">
                <ListItem button>
                  <ListItemIcon>
                    <Create />
                  </ListItemIcon>
                  <ListItemText primary="Create Restaurant" />
                </ListItem>
              </Link>
            </List>

            <List>
              <Link to="/Search" className="link">
                <ListItem button>
                  <ListItemIcon>
                    <Search />
                  </ListItemIcon>
                  <ListItemText primary="Search Restaurant" />
                </ListItem>
              </Link>
            </List>

            <List>
              <Link to="/Update" className="link">
                <ListItem button>
                  <ListItemIcon>
                    <Update />
                  </ListItemIcon>
                  <ListItemText primary="Update Restaurant" />
                </ListItem>
              </Link>
            </List>

            <Divider />
            <List>
              <Link to="/Cat" className="link">
                <ListItem button>
                  <ListItemIcon>
                    <Create />
                  </ListItemIcon>
                  <ListItemText primary="Create Category" />
                </ListItem>
              </Link>
            </List>

            <List>
              <Link to="/ListCat" className="link">
                <ListItem button>
                  <ListItemIcon>
                    <ViewList />
                  </ListItemIcon>
                  <ListItemText primary="List Categories" />
                </ListItem>
              </Link>
            </List>

            <Divider />

            {localStorage.getItem("login") ? (
              <List>
                <Link to="/Logout" className="link">
                  <ListItem button>
                    <ListItemIcon>
                      <ExitToApp />
                    </ListItemIcon>
                    <ListItemText primary="Logout" />
                  </ListItem>
                </Link>
              </List>
            ) : (
              <List>
                <Link to="/Login" className="link">
                  <ListItem button>
                    <ListItemIcon>
                      <LockOpen />
                    </ListItemIcon>
                    <ListItemText primary="Login" />
                  </ListItem>
                </Link>
              </List>
            )}

            <Divider />
          </div>
        </Drawer>

        <main className={classes.content}>
          <Toolbar />

          <Switch>
            <Protect exact path="/" component={Base}/>
            <Protect exact path="/Home" component={HomeRestro} />
            <Protect exact path="/List" component={ListRestro} />
            <Protect exact path="/Create" component={CreateRestro} />
            <Protect exact path="/Search" component={SearchRestro} />
            <Protect exact path="/Update" component={UpdateRestro} />
            <Protect exact path="/Cat" component={CreateCat} />
            <Protect exact path="/ListCat" component={ListCat} />

            <Route path="/Logout">
              <Logout />
            </Route>
            <Route exact path="/Login" component={Login} />

            <Protect path="/Update/:id" component={UpdateRestro} />
            <Protect path="/UpdateCat/:id" component={UpdateCat} />
          </Switch>
        </main>
      </div>
    </Router>
  );
}
