import React from "react";
import {
  Container,
  Box,
  Typography,
  TextField,
  Button,
  MenuItem,
} from "@material-ui/core";

export default class ResturantUpdate extends React.PureComponent {
  constructor(props) {
    super(props);

    this.state = {
        
            id: "",
            name: "",
            email: "",
            address: "",
            Rating: "",
            categoryId: ""
    };

    fetch("http://localhost:8080/restaurant/" + this.props.match.params.id).then((resp) => {
      resp.json().then((data) => {
        // console.log(data)
        this.setState({
            id: data.id,
            name: data.name,
            email: data.email,
            address: data.address,
            Rating: data.Rating,
            categoryId: data.categoryId    
        });
      });
    });

    this.handleChange = this.handleChange.bind();
    this.update = this.update.bind();
  }

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  update = () => {
    fetch("http://localhost:8080/restaurant/" + this.state.id, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(this.state),
    }).then((resp) => {
      resp.json().then((result) => {
        // console.log(result)
        alert("Resturant has been Updated");
      });
    });
  };

  async componentDidMount(){
      const response = await fetch("http://localhost:8080/categories/");
      const data = await response.json();
      console.log(data);
  }
  

  render() {    

    return (
      <Container maxWidth="sm">
        <Box
          bgcolor="white"
          boxShadow="2"
          borderRadius="12px"
          textAlign="center"
          p="24px"
          mt="50px"
        >
          <Typography variant="h5" color="primary">
            RESTAURANT UPDATE
          </Typography>

          <TextField
            label="Restaurant Name"
            placeholder={this.state.name}
            id="outlined-size-small"
            type="name"
            name="name"
            onChange={this.handleChange}
            variant="outlined"
            fullWidth
            color="secondary"
            margin="normal"
            size="small"
          />

          <TextField
            label="Restaurant Email Id"
            id="outlined-size-small"
            placeholder={this.state.email}
            type="email"
            name="email"
            onChange={this.handleChange}
            variant="outlined"
            fullWidth
            color="secondary"
            margin="normal"
            size="small"
          />

          <TextField
            label="Restaurant Address"
            id="outlined-size-small"
            type="address"
            name="address"
            placeholder={this.state.address}
            onChange={this.handleChange}
            variant="outlined"
            fullWidth
            color="secondary"
            margin="normal"
            size="small"
          />

          <TextField
            label="Restaurant Rating"
            id="outlined-size-small"
            type="Rating"
            name="Rating"
            placeholder={this.state.Rating}
            onChange={this.handleChange}
            variant="outlined"
            fullWidth
            color="secondary"
            margin="normal"
            size="small"
          />

          <TextField
            label="Restaurant Category"
            id="outlined-size-small"
            type="categoryId"
            name="categoryId"
            placeholder={this.state.categoryId}
            onChange={this.handleChange}
            variant="outlined"
            fullWidth
            color="secondary"
            margin="normal"
            size="small"
          />

        
            
           <TextField
            id="standard-select-currency"
            select
            label="Restaurant Category"
            name="categoryId"
            value={this.state.categoryId}
            onChange={this.handleChange}
            helperText="Select The Category"
          >
            {data.map((option,i) => (
              <MenuItem key={option.id} value={option.id}>
                {option.name}
              </MenuItem>
            ))}
          </TextField>
         

          <br />
          <br />
          <Button
            disableElevation
            variant="contained"
            color="primary"
            fullWidth
            onClick={this.update}
          >
            Update Restaunrant
          </Button>
        </Box>
      </Container>
    );
  }
}
