import React, { Component } from "react";
import {
  Container,
  Box,
  Typography,
  TextField,
  CircularProgress,
  Button
} from "@material-ui/core";
import logo from "../media/logo.PNG";
import Firebase from '../firebase';

export default class Login extends Component {

  constructor(props) {
      super(props)
  
      this.state = {
           email:"",
           password:"",
           show_progress:false
      };

      this.handleChange = this.handleChange.bind()
      this.login = this.login.bind()
  }
  
  handleChange = (e)=>{
      this.setState({
          [e.target.name]:e.target.value
      })
  }

  login = ()=>{
    let valid_data = true;
    this.state.email_error = null;
    this.state.password_error = null;

    if(this.state.email===""){
        this.state.email_error = "Required!";
        valid_data=false;
    }
    if(this.state.password===""){
        this.state.password_error = "Required!";
        valid_data=false;
    }
    this.setState({
        update:true
    })

    if(valid_data){
        Firebase.firestore().collection("Users")
        .where('email','==',this.state.email)
        .where('isAdmin','==',true)
        .get()
        .then(querySnapshot=>{
            if(!querySnapshot.empty){
                //login
            }else{
                this.state.email_error = "You Are Not Allowed To Login!"
                this.setState({
                    show_progress:false
                })
            }
        })
    }else{

    }
  }

  render() {
    return (
      <Container maxWidth="sm">
        <Box
          bgcolor="white"
          boxShadow="2"
          borderRadius="12px"
          textAlign="center"
          p="24px"
          mt="50px"
        >
          <img src={logo} height="50px" />
          <Typography variant="h5" color="textSecondary">
            ADMIN
          </Typography>

          <TextField
            label="Email"
            id="outlined-size-small"
            type="email"
            name="email"
            onChange={this.handleChange}
            error={this.state.email_error!=null}
            helperText={this.state.email_error}
            variant="outlined"
            fullWidth
            color="secondary"
            margin="normal"
            size="small"
          />

          <TextField
            label="Password"
            id="outlined-size-small"
            type="password"
            name="password"
            error={this.state.password_error!=null}
            helperText={this.state.password_error}
            onChange={this.handleChange}
            variant="outlined"
            fullWidth
            color="secondary"
            margin="normal"
            size="small"
          />

          <br />
          <br />

          {
          this.state.show_progress?
          <CircularProgress size={24} thickness={4} color="primary" />
          :null
          }

          <br />
          <br />
          <Button disableElevation variant="contained" color="primary" fullWidth onClick={this.login}>
            Login
          </Button>
        </Box>
      </Container>
    );
  }
}
