import React from "react";
import {
  Container,
  Box,
  Typography,
  TextField,
  CircularProgress,
  Button,
} from "@material-ui/core";

export default class ResturantUpdate extends React.PureComponent {
  constructor(props) {
    super(props);

    this.state = {
      id: "",
      name: "",
      email: "",
      address: "",
      Rating: "",
      categoryId: "",
    };

    fetch(
      "http://localhost:8080/restaurant/" + this.props.match.params.id
    ).then((resp) => {
      resp.json().then((data) => {
        // console.log(data)
        this.setState({
          id: data.id,
          name: data.name,
          email: data.email,
          address: data.address,
          Rating: data.Rating,
          categoryId: data.categoryId,
        });
      });
    });

    this.handleChange = this.handleChange.bind();
    this.update = this.update.bind();
  }

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  update = () => {
    fetch("http://localhost:8080/restaurant/" + this.state.id, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(this.state),
    }).then((resp) => {
      resp.json().then((result) => {
        // console.log(result)
        alert("Resturant has been Updated");
      });
    });
  };

  render() {
    return (
      <Container maxWidth="sm">
        <Box
          bgcolor="white"
          boxShadow="2"
          borderRadius="12px"
          textAlign="center"
          p="24px"
          mt="50px"
        >
          <Typography variant="h5" color="primary">
            RESTAURANT UPDATE
          </Typography>

          <TextField
            id="standard-full-width"
            label="Restuarant Name"
            style={{ margin: 8 }}
            placeholder={this.state.name}
            fullWidth
            margin="normal"
            color="secondary"
            margin="normal"
            size="small"
            InputLabelProps={{
              shrink: true,
            }}
          />

          <TextField
            id="standard-full-width"
            label="Restuarant Email"
            style={{ margin: 8 }}
            placeholder={this.state.email}
            fullWidth
            margin="normal"
            color="secondary"
            margin="normal"
            size="small"
            InputLabelProps={{
              shrink: true,
            }}
          />

          <TextField
            id="standard-full-width"
            label="Restuarant Address"
            style={{ margin: 8 }}
            placeholder={this.state.address}
            fullWidth
            margin="normal"
            color="secondary"
            margin="normal"
            size="small"
            InputLabelProps={{
              shrink: true,
            }}
          />

          <TextField
            id="standard-full-width"
            label="Restuarant Rating"
            style={{ margin: 8 }}
            placeholder={this.state.Rating}
            fullWidth
            margin="normal"
            color="secondary"
            margin="normal"
            size="small"
            InputLabelProps={{
              shrink: true,
            }}
          />

          <TextField
            id="standard-full-width"
            label="Restuarant Category"
            style={{ margin: 8 }}
            placeholder={this.state.catego}
            fullWidth
            margin="normal"
            color="secondary"
            margin="normal"
            size="small"
            InputLabelProps={{
              shrink: true,
            }}
          />

          <br />
          <br />
          <Button
            disableElevation
            variant="contained"
            color="primary"
            fullWidth
            onClick={this.update}
          >
            Update Restaunrant
          </Button>
        </Box>
      </Container>
    );
  }
}
