import React, { Component } from "react";
import {
  Container,
  Box,
  Typography,
  TextField,
  CircularProgress,
  Button
} from "@material-ui/core";
import logo from "../media/logo.PNG";
import firebase from '../firebase';

export default class Login extends Component {

  constructor(props) {
      super(props)
  
      this.state = {
           email:"",
           Password:"",
           show_progress:false
      };
  }
  
  handleChange = (e)=>{
      this.setState({
          
      })
  }

  render() {
    return (
      <Container maxWidth="sm">
        <Box
          bgcolor="white"
          boxShadow="2"
          borderRadius="12px"
          textAlign="center"
          p="24px"
          mt="50px"
        >
          <img src={logo} height="50px" />
          <Typography variant="h5" color="textSecondary">
            ADMIN
          </Typography>

          <TextField
            label="Email"
            id="outlined-size-small"
            variant="outlined"
            fullWidth
            name="email"
            onChange={this.handleChange}
            color="secondary"
            margin="normal"
            size="small"
          />

          <TextField
            label="Password"
            id="outlined-size-small"
            variant="outlined"
            fullWidth
            name="email"
            onChange={this.handleChange}
            color="secondary"
            margin="normal"
            size="small"
          />

          <br />
          <br />

          {
          this.state.show_progress?
          <CircularProgress size={24} thickness={4} color="primary" />
          :null
          }

          <br />
          <br />
          <Button disableElevation variant="contained" color="primary" fullWidth>
            Login
          </Button>
        </Box>
      </Container>
    );
  }
}
