import React from "react";
import {
  Container,
  Box,
  Typography,
  TextField,
  CircularProgress,
  Button,
} from "@material-ui/core";

export default class ResturantUpdate extends React.PureComponent {
  constructor(props) {
    super(props);

    this.state = {
      details: [
        {
          id: "",
          name: "",
          email: "",
          address: "",
          Rating: "",
          categoryId: "",
        },
      ],
      cat:null
    };

    fetch(
      "http://localhost:8080/restaurant/" + this.props.match.params.id
    ).then((resp) => {
      resp.json().then((data) => {
        // console.log(data)
        this.setState({
          id: data.details.id,
          name: data.name,
          email: data.email,
          address: data.address,
          Rating: data.Rating,
          categoryId: data.categoryId,
        });
      });
    });

    fetch("http://localhost:8080/categories").then((resp) => {
      resp.json().then((data) => {
        console.log(data);
        this.setState({
          list: data,
        });
      });
    });

    this.handleChange = this.handleChange.bind();
    this.update = this.update.bind();
  }

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  update = () => {
    fetch("http://localhost:8080/restaurant/" + this.state.id, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(this.state.details),
    }).then((resp) => {
      resp.json().then((result) => {
        // console.log(result)
        alert("Resturant has been Updated");
      });
    });
  };

  render() {
    return (
      <Container maxWidth="sm">
        <Box
          bgcolor="white"
          boxShadow="2"
          borderRadius="12px"
          textAlign="center"
          p="24px"
          mt="50px"
        >
          <Typography variant="h5" color="primary">
            RESTAURANT UPDATE
          </Typography>

          <TextField
            label="Restaurant Name"
            placeholder={this.state.name}
            id="outlined-size-small"
            type="name"
            name="name"
            onChange={this.handleChange}
            variant="outlined"
            fullWidth
            color="secondary"
            margin="normal"
            size="small"
          />

          <TextField
            label="Restaurant Email Id"
            id="outlined-size-small"
            placeholder={this.state.email}
            type="email"
            name="email"
            onChange={this.handleChange}
            variant="outlined"
            fullWidth
            color="secondary"
            margin="normal"
            size="small"
          />

          <TextField
            label="Restaurant Address"
            id="outlined-size-small"
            type="address"
            name="address"
            placeholder={this.state.address}
            onChange={this.handleChange}
            variant="outlined"
            fullWidth
            color="secondary"
            margin="normal"
            size="small"
          />

          <TextField
            label="Restaurant Rating"
            id="outlined-size-small"
            type="Rating"
            name="Rating"
            placeholder={this.state.Rating}
            onChange={this.handleChange}
            variant="outlined"
            fullWidth
            color="secondary"
            margin="normal"
            size="small"
          />

          <TextField
            label="Restaurant Category"
            id="outlined-size-small"
            type="categoryId"
            name="categoryId"
            placeholder={this.state.categoryId}
            onChange={this.handleChange}
            variant="outlined"
            fullWidth
            color="secondary"
            margin="normal"
            size="small"
          />

          <TextField
            id="standard-select-currency"
            select
            label="Restaurant Category"
            name="categoryId"
            value={this.state.categoryId}
            onChange={this.handleChange}
            helperText="Select The Category"
          >
            {currencies.map((option) => (
              <MenuItem key={option.value} value={option.value}>
                {option.label}
              </MenuItem>
            ))}
          </TextField>

          <br />
          <br />
          <Button
            disableElevation
            variant="contained"
            color="primary"
            fullWidth
            onClick={this.update}
          >
            Update Restaunrant
          </Button>
        </Box>
      </Container>
    );
  }
}
