import React from 'react';
import '../App.css';
import { Table } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEdit, faTrash } from '@fortawesome/free-solid-svg-icons'
import {
  CircularProgress,

} from "@material-ui/core";

export default class ResturantList extends React.PureComponent {
    state = {
        list: null,
        cat:null
    }
    componentDidMount() {
        this.refresh()
    }

    refresh = () => {
        fetch('http://localhost:8080/restaurant').then((resp) => {
            resp.json().then((data) => {
                // console.log(data)
                this.setState({
                    list: data
                })
            })
        })
    }
    delete = (key) => {
        // console.log(key)
        fetch('http://localhost:8080/restaurant/' + key, {
            method: 'DELETE',
            // headers: {
            //     'Content-Type': 'application/json'
            // },
            body: JSON.stringify(this.state)
        }).then((resp) => {
            resp.json().then((result) => {
                // console.log(result)
                alert('Resturant has been Deleted')
                this.refresh()
            })
        })
    }
    render() {
        return (
            <div>
                
                {
                    this.state.list ? 
                        <div>
                            <Table striped bordered hover>
                                <thead className="thead-dark">
                                    <tr>
                                        <th>Serial No.</th>
                                        <th>Name</th>
                                        <th>Email</th>
                                        <th>Address</th>
                                        <th>Rating</th>
                                        <th>Category</th>
                                        <th>Operation</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {
                                        this.state.list.map((item, i) =>
                                            <tr key={i}>
                                                <td>{item.id}</td>
                                                <td>{item.name}</td>
                                                <td>{item.email}</td>
                                                <td>{item.address}</td>
                                                <td>{item.Rating}</td>
                                                <td>{item.categoryId}</td>

                                                <td><Link to={'/Update/' + item.id}><FontAwesomeIcon icon={faEdit} color='orange' /></Link>
                                                    <span onClick={() => this.delete(item.id)}><FontAwesomeIcon icon={faTrash} color='red' /></span>
                                                    {/* onClick={this.deleteHandler.bind(this, item.id) */}
                                                </td>
                                            </tr>
                                        )
                                    }
                                </tbody>
                            </Table>
                        </div>
                        :
                        <h3 style={{color:'red'}}>Please Wait ...<br/><br/><br/>
                        <CircularProgress size={40} thickness={8} color="primary" />      
                        </h3>  
                }
            </div>
        );
    }
}
