import React, { Component } from "react";
import {
  Container,
  Box,
  Typography,
  TextField,
  CircularProgress,
  Button,
} from "@material-ui/core";
import logo from "../media/logo.PNG";

export default class Login extends Component {
  
  state = {
    email: "",
    password: "",
    list: null,
    show_progress: true,
  };

  componentDidMount() {
    fetch("http://localhost:8000/login").then((resp) => {
      resp.json().then((data) => {
        console.log(data);
        this.setState({
          list: data,
        });
      });
    });
  }

  login = () => {
    fetch("http://localhost:8000/login?q=" + this.state.name).then((resp) => {
      resp.json().then((data) => {
        //console.log('Result',data);

        let a, b;
        this.state.list.map((item, i) => (
          <div>
            {(a = item.email)}
            {(b = item.password)}
          </div>
        ));

        let valid_data = true;
        this.state.email_error = null;
        this.state.password_error = null;
    
        if (this.state.email === "") {
          this.state.email_error = "Required!";
          valid_data = false;
        }
        if (this.state.password === "") {
          this.state.password_error = "Required!";
          valid_data = false;
        }
      
    
        if (valid_data) {
          if (a == this.state.email && b == this.state.password) {
            localStorage.setItem("login", JSON.stringify(data));
            console.log(this.props.history.push("/list"));
          } else {
            alert("Please enter the correct UserName and Password");
          }
          console.log(data);
        } else {
        }

      });
    });
  }


  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  render() {
    return (
      <Container maxWidth="sm">
        <Box
          bgcolor="white"
          boxShadow="2"
          borderRadius="12px"
          textAlign="center"
          p="24px"
          mt="50px"
        >
          <img src={logo} height="50px" />
          <Typography variant="h5" color="textSecondary">
            ADMIN
          </Typography>

          <TextField
            label="Email"
            id="outlined-size-small"
            type="email"
            name="email"
            onChange={this.handleChange}
            error={this.state.email_error != null}
            helperText={this.state.email_error}
            variant="outlined"
            fullWidth
            color="secondary"
            margin="normal"
            size="small"
          />

          <TextField
            label="Password"
            id="outlined-size-small"
            type="password"
            name="password"
            error={this.state.password_error != null}
            helperText={this.state.password_error}
            onChange={this.handleChange}
            variant="outlined"
            fullWidth
            color="secondary"
            margin="normal"
            size="small"
          />

          <br />
          <br />

          {this.state.show_progress ? (
            <CircularProgress size={24} thickness={4} color="primary" />
          ) : null}

          <br />
          <br />
          <Button
            disableElevation
            variant="contained"
            color="primary"
            fullWidth
            onClick={this.login}
          >
            Login
          </Button>
        </Box>
      </Container>
    );
  }
}
