import React from "react";
import { Table, Container, Form } from "react-bootstrap";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEdit, faTrash } from "@fortawesome/free-solid-svg-icons";
import "../App.css";
import { CircularProgress } from "@material-ui/core";

export default class RestaurantsSearch extends React.PureComponent {
  state = {
    data: '',
    list:[],
    noDataFound: true,
    lastSearch: "",
  };

  componentDidMount(){
    fetch("http://localhost:8080/users").then((resp) => {
        resp.json().then((data) => {
          // console.log(data)
          this.setState({
            list: data,
          });
        });
      });
  }

  search = (e) => {
    this.setState({
      data: e.target.value,
    });
    // let key = e.target.value
    // this.setState({
    //     lastSearch: key
    // })
    if (this.state.data.length > 0) {
      fetch("http://localhost:8080/restaurant?q=" + e.target.value).then(
        (resp) => {
          resp.json().then((result) => {
            // console.log('result', result)
            if (result.length > 0 && this.state.lastSearch.length > 0) {
              this.setState({
                searchData: result,
                noDataFound: false,
              });
            }
          });
        }
      );
    } else {
      this.setState({
        noDataFound: true,
        searchData: null,
      });
    }
  };

  deleteHandler = (id) => {
    fetch("http://localhost:8080/restaurant/" + id, {
      method: "DELETE",
      // headers: {
      //     'Content-type': 'application/json'
      // },
      body: JSON.stringify(this.state),
    }).then((resp) => {
      resp.json().then((result) => {
        // console.log(result);
        alert("Resturant has been Deleted");
        this.search({
          target: {
            value: this.state.lastSearch,
          },
        });
      });
    });
  };

  render() {
    return (
      <Container>
        <h1 style={{ color: "blue", fontSize: "50px" }}>Welcome Admin</h1>
        <br />
        <h1 className="search">Restaurants Search</h1>
        <Form.Control type="text" placeholder="Enter Name Or Username To Be Searched..." onChange={(e)=>{this.setState({data:e.target.value})}} />
        <div>
        {
          this.state.data ? (
            <div>
              <Table striped bordered hover>
                <thead className="thead-dark">
                  <tr>
                    <th>User Id</th>
                    <th>Name</th>
                    <th>Username</th>
                    <th>Email</th>
                    <th>Address</th>
                    <th>Contact No.</th>
                    <th>Address</th>
                    <th>Operation</th>
                  </tr>
                </thead>
                <tbody>

                    {
                        this.state.list.filter(item => {
                                if(this.state.data == null)
                                    return item
                                else if(data.name.toLowerCase().includes(this.state.item.toLowerCase()) || data.username.toLowerCase().includes(this.state.item.toLowerCase())){
                                    return item
                            
                            }
                        }).map(item => 
                            <tr key={item.id}>
                            <td>{item.id}</td>
                            <td>{item.name}</td>
                            <td>{item.username}</td>
                            <td>{item.email}</td>
                            <td>{item.contact}</td>
                            <td>{item.address}</td>
                            <td>
                            <Link to={"/UpdateUser/" + item.id}>
                                <FontAwesomeIcon icon={faEdit} />
                            </Link>
                            <span
                                onClick={this.deleteHandler.bind(this, item.id)}
                            >
                                <FontAwesomeIcon icon={faTrash} color="red" />
                            </span>
                            </td>
                        </tr>
                        )
                        
                    }

                </tbody>
              </Table>
            </div>
          ) : 
            <div>
              {" "}
              <br />
              <h3 style={{color:"red"}}>User Not Found In Our Database</h3>
              <br />
              <CircularProgress size={40} thickness={6} color="primary" />
            </div>
      }
        </div>
      </Container>
    );
  }
}
