import React, { Component } from "react";
import {
  Container,
  Box,
  Typography,
  TextField,
  Button,
  MenuItem,
} from "@material-ui/core";

export default class Create extends Component {

  state = {
    id: null,
    name: null,
    price: null,
    timing: null,
  };



  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  create = () => {
    fetch("http://localhost:8080/categories", {
      method: "post",
      headers: {
        "Content-Type": "application/json",
      },
        body: JSON.stringify(this.state),
    }).then((resp) => {
      resp.json().then((result) => {
        // console.log(result)
        alert("Category has been Added");
      });
    });
  };

  render() {
    return (
      <Container maxWidth="sm">

        <h1 style={{ color: "blue", fontSize: "50px" }}>Welcome Admin</h1>
        <br />
        <Box
          bgcolor="white"
          boxShadow="2"
          borderRadius="12px"
          textAlign="center"
          p="24px"
          mt="50px"
        >
          <Typography variant="h5" color="primary">
            CREATE CATEGORY
          </Typography>

          <TextField
            label="category"
            placeholder={this.state.id}
            id="outlined-size-small"
            type="id"
            name="id"
            onChange={this.handleChange}
            variant="outlined"
            fullWidth
            color="secondary"
            margin="normal"
            size="small"
          />

          <TextField
            label="Availability"
            placeholder={this.state.name}
            id="outlined-size-small"
            type="name"
            name="name"
            onChange={this.handleChange}
            variant="outlined"
            fullWidth
            color="secondary"
            margin="normal"
            size="small"
          />

          <TextField
            label="Start Price"
            id="outlined-size-small"
            type="price"
            name="price"
            onChange={this.handleChange}
            variant="outlined"
            fullWidth
            color="secondary"
            margin="normal"
            size="small"
          />

          <TextField
            label="Timings"
            id="outlined-size-small"
            type="time"
            name="time"
            onChange={this.handleChange}
            variant="outlined"
            fullWidth
            color="secondary"
            margin="normal"
            size="small"
          />

          <br />
          <br />
          <Button
            disableElevation
            variant="contained"
            color="primary"
            fullWidth
            onClick={this.create}
          >
            ADD CATEGORY
          </Button>
        </Box>
      </Container>
    );
  }
}
