import React from 'react';
import {
    Container,
    Box,
    Typography,
    TextField,
    CircularProgress,
    Button
  } from "@material-ui/core";
  import logo from "../media/logo.PNG";

export default class ResturantUpdate extends React.PureComponent {
  
    constructor(props) {
        super(props)
    
        this.state = {
             id:"",
             name:"",
             email:"",
             address:"",
             Rating:"",
             categoryId:"",

             list:null,
             show_progress:false
        };
  
  
        fetch('http://localhost:8080/resturant/' + this.props.match.params.id).then((resp) => {
            resp.json().then((data) => {
                // console.log(data)
                this.setState({
                    id: data.id,
                    name: data.name,
                    email: data.email,
                    address: data.address,
                    Rating: data.Rating,
                    categoryId:data.categoryId,
                })
            })
        })
  
        this.handleChange = this.handleChange.bind()
        this.login = this.login.bind()
    }
    
  
  
    handleChange = (e)=>{
        this.setState({
            [e.target.name]:e.target.value
        })
    }
  
    update=()=>{
        fetch('http://localhost:8080/resturant/'+ this.state.id, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(this.state)
        }).then((resp) => {
            resp.json().then((result) => {
                // console.log(result)
                alert('Resturant has been Updated')
            })
        })
    }


    render() {
        return (
            <div>
                <h1 class={{color:'red'}}> Resturant Update </h1>
               {/* <div>
                    <input placeholder='Resturant Name' onChange={(e) => this.setState({ name: e.target.value })} value={this.state.name} /><br /><br />
                    <input placeholder='Resturant Email' onChange={(e) => this.setState({ email: e.target.value })} value={this.state.email} /><br /><br />
                    <input placeholder='Resturant Rating' onChange={(e) => this.setState({ Rating: e.target.value })} value={this.state.Rating} /><br /><br />
                    <input placeholder='Resturant Address' onChange={(e) => this.setState({ Address: e.target.value })} value={this.state.Address} /><br /><br />
                    <button onClick={this.update}>Update Resturant</button>
                </div>
               */}

        <Container maxWidth="sm">
        <Box
          bgcolor="white"
          boxShadow="2"
          borderRadius="12px"
          textAlign="center"
          p="24px"
          mt="50px"
        >
          <Typography variant="h5" color="primary">
            RESTAURANT UPDATE
          </Typography>

          <TextField
            label="Restaurant Name"
            id="outlined-size-small"
            type="name"
            name="name"
            onChange={this.handleChange}
            error={this.state.email_error!=null}
            helperText={this.state.email_error}
            variant="outlined"
            fullWidth
            color="secondary"
            margin="normal"
            size="small"
          />

          <TextField
            label="Email"
            id="outlined-size-small"
            type="email"
            name="email"
            onChange={this.handleChange}
            error={this.state.email_error!=null}
            helperText={this.state.email_error}
            variant="outlined"
            fullWidth
            color="secondary"
            margin="normal"
            size="small"
          />

          <TextField
            label="Password"
            id="outlined-size-small"
            type="password"
            name="password"
            error={this.state.password_error!=null}
            helperText={this.state.password_error}
            onChange={this.handleChange}
            variant="outlined"
            fullWidth
            color="secondary"
            margin="normal"
            size="small"
          />

          <br />
          <br />

          {
          this.state.show_progress?
          <CircularProgress size={24} thickness={4} color="primary" />
          :null
          }

          <br />
          <br />
          <Button disableElevation variant="contained" color="primary" fullWidth onClick={this.update}>
            Update Restaunrant
          </Button>
        </Box>
      </Container>

            </div>
        );
    }
}
