import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Drawer from "@material-ui/core/Drawer";
import AppBar from "@material-ui/core/AppBar";
import CssBaseline from "@material-ui/core/CssBaseline";
import Toolbar from "@material-ui/core/Toolbar";
import List from "@material-ui/core/List";
import Typography from "@material-ui/core/Typography";
import Divider from "@material-ui/core/Divider";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import { Home } from "@material-ui/icons";
import { Create } from "@material-ui/icons";
import { Update } from "@material-ui/icons";
import { ViewList } from "@material-ui/icons";
import { Search } from "@material-ui/icons";
import { ExitToApp } from "@material-ui/icons";
import logo from "../media/logo.PNG";
import {Link, Switch, Route} from 'react-router-dom'

const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
  },
  appBar: {
    zIndex: theme.zIndex.drawer + 1,
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0,
  },
  drawerPaper: {
    width: drawerWidth,
  },
  drawerContainer: {
    overflow: "auto",
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },
  link: {
    textDecoration:'none',
    color: theme.palette.text.primary
  },
}));

export default function ClippedDrawer() {
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <CssBaseline />
      <AppBar position="fixed" className={classes.appBar}>
        <Toolbar>
          <Typography variant="h6" noWrap>
            <img src={logo} height="40px" style={{ marginRight: "16px" }} />
            <Typography variant="h4" display="inline">
              Tamra Restaurant Admin
            </Typography>
          </Typography>
        </Toolbar>
      </AppBar>
      <Drawer
        className={classes.drawer}
        variant="permanent"
        classes={{
          paper: classes.drawerPaper,
        }}
      >
        <Toolbar />
        <div className={classes.drawerContainer}>

          <List>
            <Link to="/Home" className={classes.link}>
            <ListItem button>
              <ListItemIcon>
                <Home />
              </ListItemIcon>
              <ListItemText primary="Home" />
            </ListItem>
            </Link>
          </List>

          <List>
          <Link to="/List" className={classes.link}>
            <ListItem button>
              <ListItemIcon>
                <ViewList />
              </ListItemIcon>
              <ListItemText primary="List Restaurant" />
            </ListItem>
          </Link>
          </List>

          <List>
          <Link to="/Create" className={classes.link}>
            <ListItem button>
              <ListItemIcon>
                <Create />
              </ListItemIcon>
              <ListItemText primary="Create Restaurant" />
            </ListItem>
          </Link>
          </List>

          <List>
          <Link to="/Search" className={classes.link}>
            <ListItem button>
              <ListItemIcon>
                <Search />
              </ListItemIcon>
              <ListItemText primary="Search Restaurant" />
            </ListItem>
          </Link>
          </List>

          <List>
          <Link to="/Update" className={classes.link}>
            <ListItem button>
              <ListItemIcon>
                <Update />
              </ListItemIcon>
              <ListItemText primary="Update Restaurant" />
            </ListItem>
          </Link>
          </List>

          <Divider />
          <List>
          <Link to="/logout" className={classes.link}>
            <ListItem button>
              <ListItemIcon>
                <ExitToApp />
              </ListItemIcon>
              <ListItemText primary="Logout" />
            </ListItem>
          </Link>
          </List>
          <Divider />
        </div>
      </Drawer>

       

      <main className={classes.content}>
        <Toolbar />
        <div style={{ textAlign: "center" }}>
          <h1 style={{ color: "blue", fontSize: "50px" }}>Welcome Admin</h1>
          <br />
          <h2 style={{ color: "red", fontSize: "20px" }}>
            What You Have In Your Mind Today...
          </h2>
        </div>
        <Switch>
            <Route exact path="/Home">Home</Route>
            <Route exact path="/List">List Restaurant</Route>
            <Route exact path="/Create">Create Restaurant</Route>
            <Route exact path="/Search">Search Restaurant</Route>
            <Route exact path="/Update">Update Restaurant</Route>
            <Route exact path="/Logout">Logout</Route>
        </Switch>
      </main>
    </div>
  );
}
