import React, { Component } from 'react'
import {
    Container,
    Box,
    Typography,
    TextField,
    Button,
    MenuItem,
} from "@material-ui/core";

export default class Create extends Component {

    state = {
        name: null,
        email: null,
        address: null,
        Rating: null,
        categoryId: null,
        list:[]
    }

    componentDidMount(){
        fetch('http://localhost:8080/categories/').then((resp) => {
            resp.json().then((data) => {
                console.log(data)
                this.setState({
                    list:data
                })
                console.log(data)
            })
        })
    }

    create = () => {
        fetch('http://localhost:8000/resturant', {
            method: 'post',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({id:this.state.id, name:this.state.name, email:this.state.email, address:this.state.address, Rating:this.state.Rating, categoryId:this.state.categoryId})
        }).then((resp) => {
            resp.json().then((result) => {
                // console.log(result)
                alert('Resturant has been Added')
            })
        })
    }

        render() {   
            return (
              <Container maxWidth="sm">
                <Box
                  bgcolor="white"
                  boxShadow="2"
                  borderRadius="12px"
                  textAlign="center"
                  p="24px"
                  mt="50px"
                >
                  <Typography variant="h5" color="primary">
                    RESTAURANT UPDATE
                  </Typography>
        
                  <TextField
                    label="Restaurant Name"
                    placeholder={this.state.name}
                    id="outlined-size-small"
                    type="name"
                    name="name"
                    onChange={this.handleChange}
                    variant="outlined"
                    fullWidth
                    color="secondary"
                    margin="normal"
                    size="small"
                  />
        
                  <TextField
                    label="Restaurant Email Id"
                    id="outlined-size-small"
                    placeholder={this.state.email}
                    type="email"
                    name="email"
                    onChange={this.handleChange}
                    variant="outlined"
                    fullWidth
                    color="secondary"
                    margin="normal"
                    size="small"
                  />
        
                  <TextField
                    label="Restaurant Address"
                    id="outlined-size-small"
                    type="address"
                    name="address"
                    placeholder={this.state.address}
                    onChange={this.handleChange}
                    variant="outlined"
                    fullWidth
                    color="secondary"
                    margin="normal"
                    size="small"
                  />
        
                  <TextField
                    label="Restaurant Rating"
                    id="outlined-size-small"
                    type="Rating"
                    name="Rating"
                    placeholder={this.state.Rating}
                    onChange={this.handleChange}
                    variant="outlined"
                    fullWidth
                    color="secondary"
                    margin="normal"
                    size="small"
                  />
        
                <TextField
                  id="outlined-size-small"
                  select
                  label="Restaurant Category"
                  name="categoryId"
                  type="categoryId"
                  placeholder={this.state.categoryId}
                  onChange={this.handleChange}
                  variant="outlined"
                  fullWidth
                  color="secondary"
                  margin="normal"
                  size="small"
                >
                 
                 {
                     this.state.list.map(item => (
                         <MenuItem key = {item.id} value={item.id}>
                             {item.id}
                        </MenuItem>
                     ))
                 }
                </TextField>
        
                  <br />
                  <br />
                  <Button
                    disableElevation
                    variant="contained"
                    color="primary"
                    fullWidth
                    onClick={this.create}
                  >
                    Create Restaurant
                  </Button>
                </Box>
              </Container>
            );
          }
    
}
