import React, { Component } from 'react';


export default class Logout extends Component {

    
    constructor(props){
        super(props)
            localStorage.clear("login")   
    }

    render(){   
        return (
            <div>
                {
                    this.props.history.push("/login")
                }
                {/* or we can use      <Redirect to="/login"/>     */}
            </div>        
        );
    }
}
