import React, { Component } from 'react'
import Navbar2 from './Navbar2'
import Footer from './Footer'
import { Breadcrumb } from 'react-bootstrap';
import '../CSS/style.css'

export default class Services extends Component {

    constructor(props){
        super(props)
        this.state={
            name:'',
            email:'',
            contact:'',
            address:'',
            category:'',
            restaurant:'',
            userId:'',
            list:[], //for category
            list2:[], // for userId session
            reslist:[{
                name:'none'
            }]  // for restaurant
        }
        
        this.state.list2= JSON.parse( localStorage.getItem('login'));  
        this.state.userId = this.state.list2[0].id     
        
        //to fetch categories
        fetch('http://localhost:8080/categories').then((resp) => {
            resp.json().then((data) => {
                this.setState({
                    list:data
                })
            })
        })

        

    }

    change= (e) => {

        //fetching restaurant
        
        fetch(`http://localhost:8080/categories/${this.state.category}/restaurant`).then((resp) => {
            resp.json().then((data) => {
                console.log(data)
                this.setState({
                    reslist:data
                })
            })
        })
       

        this.setState({
            restaurant: e.target.value
        })
    }

    render() {
        return (

            <div>

            <div className="main">
                <Navbar2/>
                <Breadcrumb>
                     <Breadcrumb.Item href="/"><h5 className="bread">Home</h5></Breadcrumb.Item>
                        <Breadcrumb.Item href="/profile">
                            <h5 className="bread">Profile</h5>
                        </Breadcrumb.Item>
                    <Breadcrumb.Item active>Data</Breadcrumb.Item>
                </Breadcrumb>

                <div className="container form-content">		
                    <div className="header_content">
                        <h2> Book Your Table </h2>
                    </div>
                    
                    <div className="card col-md-6 main-form">
                        <div className="card-header">
                            <h3 className="mt-2" style={{color:"red"}}> Booking Form  </h3>
                        </div>
                        <div className="card-body">
                            <form className="form row">
                                <div className="form-group col-md-12">
                                    <label htmlFor="name">Name</label>
                                    <input type="text" className="form-control" name="name" onChange={(e) => this.setState({name: e.target.value})}  required/>

                                </div>
                                <div className="form-group col-md-12">
                                    <label htmlFor="email">Email Id </label>
                                    <input type="email" className="form-control" name="email" onChange={(e) => this.setState({email: e.target.value})}  required/>
                                </div>
                                
                                <div className="form-group col-md-12">
                                    <label htmlFor="contact"> Mobile No. </label>
                                    <input type="text" className="form-control" name="contact" onChange={(e) => this.setState({contact: e.target.value})} required/>
                                </div>

                                <div className="form-group col-md-12">
                                    <label htmlFor="contact"> Address </label>
                                    <input type="text" className="form-control" name="address" onChange={(e) => this.setState({address: e.target.value})} required/>
                                </div>

                                <div className="form-group col-md-12">
                                <label>Select Your Category</label>
                                <select className="form-control" onChange={(e) => this.setState({restaurant: e.target.value})}>
                                       {
                                           this.state.list.map((item,i) => 
                                               <option key={item.id}>
                                                   {item.id}
                                               </option>
                                            
                                           )
                                       }
                                </select>
                                </div>
                                {this.update}
                                <div className="form-group col-md-12">
                                <label>Select Your Restaurant</label>
                                <select className="form-control" onChange={this.change}>
                                       {
                                           this.state.reslist.map((res,i) => 
                                               <option key={res.id}>
                                                   {res.name}
                                               </option>
                                            
                                           )
                                       }
                                </select>
                                </div>
                        

                                <div className="form-group col-md-6">
                                    <label htmlFor="uname1">Present Class </label>
                                    <input type="text" className="form-control" name="uname1" id="uname1" required=""/>

                                </div>
                                <div className="form-group col-md-6">
                                    <label htmlFor="name">Coaching </label>
                                    <input type="text" className="form-control" name="uname1" required=""/>
                                </div>
                                <button type="submit" className="btn btn-success btn-lg col-md-6 " id="btnLogin"> Submit </button>
                            </form>
                        </div>
                    </div>
                </div>

        <Footer/>
        </div>

        </div>
        )
    }
}
